#include <iostream>
#include <string>
#include <vector>
#include <unordered_map>
#include <algorithm>

/*
 * Input:
  s = "barfoothefoobarman",
  words = ["foo","bar"]
Output: [0,9]
 */

using namespace std;

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        int size = words.size();
        visit = (int*)new int(size);
        vector<int> res;
        int allwlen = 0;
        for (int i = 0; i < size; i++) {
            allwlen += words[i].length();
            visit[i] = 0;
//            cout << "error" << endl;
            char c = words[i][0];
//            cout << "char " << c << endl;
            if (table.find(c) == table.end()) {
//                cout << "what" << endl;
                vector<int> *vec = new vector<int>();
                vec->push_back(i);
                table[c] = vec;
            } else {
                table[c]->push_back(i);
            }
        }
        for (int i = 0; i < s.length() - allwlen + 2; i++) {
//            cout << "roud " << i <<endl;
            for(int j = 0; j < size; j++)
                visit[j] = 0;
            int ws = 0;
            bool b = find(s, words, i, size, ws);
            if (b) res.push_back(i);
        }
        return res;
    }

    bool find(string &s, vector<string>& words, int idx, int wsize, int ws) {
        cout << "find" << endl;
        if (ws == wsize) return true;
        if (idx >= s.length()) return false;
        if (table.find(s[idx]) == table.end()) return false;
        for (vector<int>::iterator itr = table[s[idx]]->begin(); itr != table[s[idx]]->end(); itr++) {
            if (visit[*itr]) continue;
            if (!comp(s, idx, words[*itr])) continue;
            visit[*itr] = 1;
            bool b = find(s, words, idx + words[*itr].length(), wsize, ws+1);
            if (b) return b;
            visit[*itr] = 0;
        }
        return false;
    }

    bool comp(string &s, int idx, string word) {
        int wlen = word.length();
        if (idx + wlen - 1 >= s.length()) return false;
        for (int i = 0; i < wlen; i++) {
            if (word[i] != s[idx + i]) return false;
        }
        return true;
    }

    unordered_map<char, vector<int>* > table;
    int* visit;
};

